import React, { Component } from "react";
import axios from "axios"
import AnimeList from "../Anime/AnimeList";
import "./home.css";

export class Home extends Component {

  state = {
    title: "",
    animeArray: []

  }

  handleSearchAnime = async (title) => {
    try {
      //https://api.spoonacular.com/recipes/random?number=1&tags=vegetarian,dessert
      let result = await axios.get(
        `https://api.jikan.moe/v3/search/anime?q=${title}`
        );
        console.log(result)
        return result;
      } catch (e) {
        return e;
      }
    };


    handleOnChange = (event) => {
      this.setState({
        title: event.target.value,
      });
    };

    onSubmit = async (event) => {
      try {
        let results = await this.handleSearchAnime(this.state.title);
        console.log(results)

        this.setState({
          animeArray: results.data.results,
        })

      } catch (e) {
        console.log(e);
      }

    }

    render() {
      return (
        <div>
        <div className="input">
        <input
        type="text"
        placeholder="Search something..."
        name="title"
        onChange={this.handleOnChange}
        />
        <button onClick={this.onSubmit}>Search</button>
        </div>

        <div style={{
          width: 1200,
          margin: "0 auto",
          textAlign: "center",
          marginTop: "50px",
          display: "flex",
        }}
        >

        <h2>Animes </h2>
        <div style={{ textAlign: "center", marginTop: "15%", fontSize: 16 }}>
        <AnimeList animeArray={this.state.animeArray} />
        </div>



        </div>

        </div>

        );


      }


    }

    export default Home;
