import React, { Component } from "react";
import axios from "axios"
import { Carousel } from "react-bootstrap";
import AnimeList from "../Anime/AnimeList";
import "./Home.css";

export class Home extends Component {

  state = {
    title: "",
    animeArray: [],
    images: null

  }

  handleSearchAnime = async (title) => {
    try {
      //https://api.spoonacular.com/recipes/random?number=1&tags=vegetarian,dessert
      let result = await axios.get(
        `https://api.jikan.moe/v3/search/anime?q=${title}`
        );
        console.log(result)
        return result;ZZZZZZZZZZZZZZZZ
      } catch (e) {
        return e;
      }
    };


    handleOnChange = (event) => {
      this.setState({
        title: event.target.value,
      });
    };

    onSubmit = async (event) => {
      try {
        let results = await this.handleSearchAnime(this.state.title);
        console.log(results)

        this.setState({
          animeArray: results.data.results,
        })

      } catch (e) {
        console.log(e);
      }

    }

    render() {
      return (
        <div>
        <div className="input">
        <input
        type="text"
        placeholder="Search something..."
        name="title"
        onChange={this.handleOnChange}
        />
        <button onClick={this.onSubmit}>Search</button>
        </div>

        <div className="top1">

        <h2>Animes </h2>

        <Carousel>
  <Carousel.Item>
  <AnimeList animeArray={this.state.animeArray} />

    <img
      className="d-block w-100"
      src="holder.js/800x400?text=First slide&bg=373940"
      alt="First slide"
    />
    <Carousel.Caption>
      <h3>First slide label</h3>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Second slide&bg=282c34"
      alt="Second slide"
    />

    <Carousel.Caption>
      <h3>Second slide label</h3>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Third slide&bg=20232a"
      alt="Third slide"
    />

    <Carousel.Caption>
      <h3>Third slide label</h3>
      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
    </Carousel.Caption>
  </Carousel.Item>
</Carousel>

        </div>

        </div>

        );


      }

    }

    export default Home;
